    ...
}

// Examples of braced init list on a single line.
return {foo, bar};
functioncall({foo, bar});
pair<int, int> p{foo, bar};

// When you have to wrap.
some_function(
    {"assume a zero-length name before {"},
    some_other_function_parameter);
SomeType variable{
    some, other, values,
    {"assume a zero-length name before {"},
    SomeOtherType{
        "Very long string requiring the surrounding breaks.",
        some, other values},
    SomeOtherType{"Slightly shorter string",
                  some, other, values}};
SomeType variable{
    "This is too long to fit all in one line"};
SomeType m = {  // Here, you could also break before {.
    superlongvariablename1,
    superlongvariablename2,
    {short, interior, list},
    {interiorwrappinglist,
     interiorwrappinglist2}};

if (condition) {  // no spaces inside parentheses
    ...  // 4 space indent.
} else if (...) {  // The else goes on the same line as the closing brace.
  ...
} else {
  ...
}

BODY.BAD_CODE_SNIPPET:
// Not allowed - IF statement on one line when there is an ELSE clause
if (x) DoThis();
else DoThat();

BODY.CODE_SNIPPET:
if (condition)
  do_something();  // 2 space indent.

if (condition) {
  do_something();  // 2 space indent.
}

BODY.BAD_CODE_SNIPPET:
// Not allowed - curly on IF but not ELSE
if (condition) {
  foo;
} else
  bar;

// Not allowed - curly on ELSE but not IF
if (condition)
  foo;
else {
  bar;
}

BODY.CODE_SNIPPET:
// Curly braces around both IF and ELSE required because
// one of the clauses used braces.
if (condition) {
  foo;
} else {
  bar;
}

STYLEPOINT.SUMMARY:
Switch statements may use braces for blocks. Annotate non-trivial
fall-through between cases. Empty loop bodies should use {}
or continue.

BODY.CODE_SNIPPET:
switch (var) {
  case 0: {  // 2 space indent
    ...      // 4 space indent
    break;
  }
  case 1: {
    ...
    break;
  }
  default: {
    assert(false);
  }
}

while (condition) {
  // Repeat test until it returns false.
}
for (int i = 0; i < kSomeNumber; ++i) {}  // Good - empty body.
while (condition) continue;  // Good - continue indicates no logic.

x = *p;
p = &x;
x = r.y;
x = r->y;

// These are fine, space preceding.
char *c;
const std::string &str;

// These are fine, space following.
char* c;    // but remember to do "char* c, *d, *e, ...;"!
const std::string& str;

if (this_one_thing > this_other_thing &&
    a_third_thing == a_fourth_thing &&
    yet_another && last_one) {
  ...
}

int x = 3;
int x(3);
int x{3};
std::string name = "Some Name";
std::string name("Some Name");
std::string name{"Some Name"};

// Good - directives at beginning of line
  if (lopsided_score) {
#if DISASTER_PENDING      // Correct -- Starts at beginning of line
    DropEverything();
# if NOTIFY               // OK but not required -- Spaces after #
    NotifyClient();
# endif
#endif
    BackToNormal();
  }

BODY.BAD_CODE_SNIPPET:
// Bad - indented directives
  if (lopsided_score) {
    #if DISASTER_PENDING  // Wrong!  The "#if" should be at beginning of line
    DropEverything();
    #endif                // Wrong!  Do not indent "#endif"
    BackToNormal();
  }

BODY.CODE_SNIPPET:
class MpmClass : public OtherClass {
 public:      // Note the 1 space indent!
  MpmClass();  // Regular 2 space indent.
  explicit MpmClass(int var);
  ~MpmClass() {}

  void SomeFunction();
  void SomeFunctionThatDoesNothing() {
  }

  void set_some_var(int var) { some_var_ = var; }
  int some_var() const { return some_var_; }

 private:
  bool SomeInternalFunction();

  int some_var_;
  int some_other_var_;
  DISALLOW_COPY_AND_ASSIGN(MpmClass);
};

// When it requires multiple lines, indent 4 spaces, putting the colon on
// the first initializer line:
MpmClass::MpmClass(int var)
    : some_var_(var),             // 4 space indent
      some_other_var_(var + 1) {  // lined up
  ...
  do_something();
  ...
}

